function solutionsItemsToggleClass(){document.querySelectorAll(".solutions-item").forEach(e=>{e.addEventListener("click",e=>{e.target.classList.toggle("active")})})}function checkboxToggle(){let e=document.querySelector("#agreed-check"),c=document.querySelector(".checkbox-inside");e.checked?c.classList.add("active"):c.classList.remove("active")}solutionsItemsToggleClass(),checkboxToggle();